class Akun extends StatefulWidget {
  const Akun({super.key});

  @override
  State<Akun> createState() => _AkunState();
}

class _AkunState extends State<Akun> {
  final double coverHeight = 280;
  final double ProfileHeight =140;
  @override
  Widget build(BuildContext context) {
    
    return Scaffold(
    body:ListView(
      padding: EdgeInsets.zero,
      children: <Widget>[
          buildtop(),
          buildcontent(),
      ],
    ),
    );
  }
  Widget buildtop(){
      final top = coverHeight - ProfileHeight /1;
      return Stack(
      clipBehavior: Clip.none,
      alignment: Alignment.center,
    children: [
      buildcoverImage(),
      Positioned(
        top: top,
        child: buildProfileImage(),
        ),
    ],
    );
    }
  Widget buildcoverImage() => Container(
      color: Colors.transparent,
      child: Image.network('https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/faa48d2d-12c2-43d1-bf23-b5e99857825b/ddanutv-39cde392-7484-42ec-8b83-f00e58094746.png/v1/fill/w_622,h_350,q_70,strp/dance_of_lights_by_ellysiumn_ddanutv-350t.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..3USm-rYv-0y6spWqrtA0V2B4-tpDl1qMHX25VTjDd94',
      width: double.infinity,
      height: coverHeight,
      fit: BoxFit.fill,
      ),
  );
  Widget buildProfileImage()=> CircleAvatar(
    radius: ProfileHeight /2,
    backgroundColor: Colors.blue,
    backgroundImage: NetworkImage('data:image/jpeg;base64,'),
  );

  Widget buildcontent() =>Container(
    child: Column(mainAxisAlignment: MainAxisAlignment.center,
    children: [SizedBox(height: 10,), Text('tes123',style: TextStyle(fontSize: 20,fontWeight: FontWeight.bold),),
      Text('tes123@gmail.com',style: TextStyle(fontSize: 20,fontWeight: FontWeight.bold),)
    ]
    ,),
  ); 

}